import {Image} from 'react-native';
import React from "react"; // Importação da biblioteca "React"
import styled from "styled-components/native";


const Header = styled.View`
flex-direction:row;
background-color: #DDD;
height: 500px;
flex-wrap: wrap;
justify-content: center;
`;

const Quadrado = styled.View`

background-color: ${props => props.cor};
width:500px;
height:300px;
justify-content:center;
align-items:center;
`;

const Pagina = styled.View`
  flex:1;

`;

function App() {
  return ( 

    <Pagina>
      <Header>
      <Quadrado  cor="white"></Quadrado>
      <Quadrado  cor="white">

        <Image source={require('./src/imagens/sedan-car.png')}
           style={{width: 400, height: 300, backgroundColor:'#AAA'}}
           resizeMode='stretch' />

      </Quadrado>

      <Quadrado  cor="blue">

        <Image source={require('./src/imagens/sedan-car.png')}
           style={{width: 1000, height: 300, backgroundColor:'#AAA'}}
           resizeMode='cover' />

      </Quadrado>



      </Header>
    </Pagina>
  );
}

export default App;
