import {Image} from 'react-native';
import React from "react"; // Importação da biblioteca "React"
import styled from "styled-components/native";


const Header = styled.View`
flex-direction:row;
background-color: #DDD;
height: 500px;
flex-wrap: wrap;
justify-content: center;
`;

const Quadrado = styled.View`

background-color: ${props => props.cor};
width:150px;
height:150px;
justify-content:center;
align-items:center;
`;

const Pagina = styled.View`
  flex:1;

`;

function App() {
  return ( 

    <Pagina>
      <Header>
      <Quadrado  cor="white"></Quadrado>
      <Quadrado  cor="white">

        <Image source={require('./src/imagens/sedan-car.png')}
           style={{width: 500, height: 500, backgroundColor:'#AAA'}}
           resizeMode='' />

      </Quadrado>
      <Quadrado  cor="blue">
         <Image source={{uri:'http://s2.glbimg.com/z_gIOSUdsxyNGClgVLYVBHBziyw=/0x0:400x400/400x400/s.glbimg.com/po/tt2/f/original/2016/05/20/new-google-favicon-logo.png'}}
           style={{width: 100, height: 100, backgroundColor:'#AAA'}}
           resizeMode='cover'
         />

      </Quadrado>


      </Header>
    </Pagina>
  );
}

export default App;
